import React from 'react'
import { Form, Input, Button } from 'semantic-ui-react'


export default class CreateNewUser extends React.Component{
    constructor(){
        super()
        this.state ={
            name : ""
        }
    }

    handleNewUser=(e)=>{
        debugger
        this.setState({name : e.currentTarget.previousSibling.children[0].value})
    }


    render(){
        return(
            <div>
                 <Form>

                    <label>First name</label>
                    <Input placeholder='First name' />
                    <Button primary onClick={this.handleNewUser}>Create</Button>

                </Form>
            </div>
        )
    }
}
