import React from 'react';
import './App.css';
import NavBar from './components/NavBar'
import Home from './components/Home'
import Profile from './components/Profile'
import Category from './components/AllCategories';
import CreateNewUser from './components/CreateNewUser';
import Login from './components/Login'
import Images from './components/Images';

export default class App extends React.Component {
  constructor(){
      super()
        this.state={
        homePage: false,
        profilePage: false,
        categoriesPage: false,
        AllCategoriesPage: false,
        CreateNewUserPage: false,  
        loginPage: true,
        imagesPage: false,
        listOfCategories: [],
        token: '',
        currentUser: [],
        allUsers: []

        }
  }
  componentDidMount=()=>{
    fetch('http://localhost:3000/api/v1/categories')
        .then(res=>res.json()).then(data => {
        this.setState({listOfCategories: data})})
    fetch('http://localhost:3000/api/v1/users')
        .then(res=>res.json()).then(data => {
        this.setState({allUsers: data})})
  }

  handleNavLogout=()=>{
    localStorage.clear()
    this.setState({
      homePage: false,
      profilePage: false,
      categoriesPage: false,
      AllCategoriesPage: false,
      CreateNewUserPage: false,  
      loginPage: true,
      imagesPage: false,
    })

  }
  handleNavProfile=()=>{
    this.setState({
      homePage: false,
      profilePage: true,
      categoriesPage: false,
      AllCategoriesPage: false,
      CreateNewUserPage: false,  
      loginPage: false,
      imagesPage: false,
    })

  }
  handleNavHome=()=>{
    this.setState({
      homePage: true,
      profilePage: false,
      categoriesPage: false,
      AllCategoriesPage: false,
      CreateNewUserPage: false,  
      loginPage: false,
      imagesPage: false,
    })

  }
  handleCreateNewUser=()=>{
    this.setState({
      homePage: false,
      profilePage: false,
      categoriesPage: false,
      AllCategoriesPage: false,
      CreateNewUserPage: true,  
      loginPage: false,
      imagesPage: false,
    })
  }
  handleImagePage=()=>{
    this.setState({
      homePage: false,
      profilePage: false,
      categoriesPage: false,
      AllCategoriesPage: false,
      CreateNewUserPage: false,  
      loginPage: false,
      imagesPage: true,
    })
  }

  handleToken=(data)=>{
    if(!data.message){
      this.setState({
        loginPage: false,
        profilePage: true,
        token: data.token,
        currentUser: data.user
      })
    }else{
      alert(data.message)
    }
  }


  handleCurrentPage=()=>{
    if(this.state.loginPage === true){
      return <Login handleToken={this.handleToken} handleCreateNewUser={this.handleCreateNewUser}/>
    }else if (this.state.homePage === true){
      return <Home categories={this.state.listOfCategories}/>
    }else if (this.state.profilePage === true){
      return <Profile allUsers={this.state.allUsers} currentUser={this.state.currentUser}/>
    }else if (this.state.CreateNewUserPage === true){
      return <CreateNewUser/>
    }else if (this.state.imagesPage === true){
      return <Images />
    }else if (this.state.categoriesPage === true){
      return <CreateNewUser/>
    }
  }




  render (){
    return (
      <div>   
        <NavBar handleNavLogout={this.handleNavLogout} handleNavProfile={this.handleNavProfile} handleNavHome={this.handleNavHome}/>

        <div>    
          {this.handleCurrentPage()}
        </div>
      </div>

    )
  }
}
