import React from 'react';
import './App.css';
import NavBar from './components/NavBar'
import Home from './components/Home'
import Profile from './components/Profile'
import Category from './components/AllCategories';
import CreateNewUser from './components/CreateNewUser';
import Login from './components/Login'

export default class App extends React.Component {
  constructor(){
      super()
        this.state={
        homePage: false,
        profilePage: false,
        categoriesPage: false,
        AllCategoriesPage: false,
        CreateNewUserPage: false,  
        loginPage: true,
        imagesPage: false,
        listOfCategories: [],
        token: '',
        currentUser: []

        }
  }
  componentDidMount=()=>{
    fetch('http://localhost:3000/api/v1/categories')
        .then(res=>res.json()).then(data => {
        this.setState({listOfCategories: data})})
  }

  handleNavLogout=()=>{
    this.setState({
      loginPage: true,
    })

  }
  handleNavProfile=()=>{
    this.setState({
      profilePage: true,
    })

  }
  handleNavHome=()=>{
    this.setState({
      homePage: true,
    })

  }


  handleToken=(data)=>{
    if(!data.message){
      this.setState({
        loginPage: false,
        profilePage: true,
        token: data.token,
        currentUser: data.user
      })
    }else{
      alert(data.message)
    }
  }


  handleCurrentPage=()=>{
    if(this.state.loginPage === true){
      return <Login handleToken={this.handleToken}/>
    }else if (this.state.homePage === true){
      return <Home categories={this.state.listOfCategories}/>
    }else if (this.state.profilePage === true){
      return <Profile/>
    }else if (this.state.CreateNewUserPage === true){
      return <CreateNewUser/>
    }
  }




  render (){
    return (
      <div>   
        <NavBar handleNavLogout={this.handleNavLogout}/>

        <div>    
          {this.handleCurrentPage()}
        </div>
      </div>

    )
  }
}
