import React from 'react'
import AllCategories from './AllCategories'
import { Button, Dimmer, Header, Image, Card ,Modal } from 'semantic-ui-react'



export default class Home extends React.Component{
    constructor(){
        super()
        this.state={
            active: false,
            open: false
        }
    }

    show = dimmer => () => this.setState({ dimmer, open: true })
    close = () => this.setState({ open: false })


    handleDimmer=()=>{
        this.setState({
            active: !this.state.active
        })
    }
    render(){
        const { open, dimmer } = this.state
        const { active } = this.state
        const content = (
          <div>
            <Header as='h2' inverted>
              Title
            </Header>

            <Button circular onClick={this.show('blurring')} size='huge' primary icon='plus' />
            <Button circular size='huge' icon='heart outline' />

          </div>



        )
        return(
            <div>

        <Card.Group> 
            <Card raised className="card" color='red' image={
            <Dimmer.Dimmable

                as={Image}
                dimmed={active}
                dimmer={{ active, content }}
                onMouseEnter={this.handleDimmer}
                onMouseLeave={this.handleDimmer}
                size='medium'
                src='border.jpg'
                />
            } /> 
           {this.props.categories.map(category => <AllCategories category={category}/>)}
        </Card.Group>
        </div>
        )
    }
}
